function fnSistemaController(e,i){e.getEquipes=function(){i.get("/ws/equipes").success(function(i){e.listaEquipes=i})},e.getEquipes(),e.getEstadios=function(){i.get("/ws/estadios").success(function(i){e.listaEstadios=i})},e.getEstadios(),e.cancelaEquipe=function(){e.equipe=[]},e.saveEquipe=function(){i.post("/admin/save_equipe",e.equipe).success(function(i){e.cancelaEquipe(),e.getEquipes(),e.avisoEquipe={"class":"alert-success",mensagem:"A equipe foi GRAVADA com sucesso"},angular.element("#avisoEquipe").fadeIn("slow")})},e.editaEquipe=function(i){angular.element(document).scrollTop(0),e.equipe=i,e.equipe.id_estadio=i.id_estadio},e.chamaConfirmaDelete=function(i){e.mdEquipe=i,angular.element("#mdConfirmaDelete").modal("show")},e.deleteEquipe=function(s){i["delete"]("/admin/delete_equipe/"+s.id).success(function(){angular.element("#mdConfirmaDelete").modal("hide"),e.getEquipes()})}}fnSistemaController.$inject=["$scope","$http"],angular.module("app").controller("sistemaController",fnSistemaController);