function fnSistemaController(e,t){e.getEstadios=function(){t.get("/ws/estadios").success(function(t){e.listaEstadios=t})},e.getEstadios(),e.cancelaEstadio=function(){angular.element(".limpa-form").val("").prop("checked",!1)},e.saveEstadio=function(){t.post("/admin/save_estadio",e.estadio).success(function(t){e.cancelaEstadio(),e.getEstadios(),e.avisoEstadio={"class":"alert-success",mensagem:"O est√°dio foi GRAVADO com sucesso"},angular.element("#avisoEstadio").fadeIn("slow")})},e.editaEstadio=function(t){angular.element(document).scrollTop(0),e.estadio=t},e.chamaConfirmaDelete=function(t){e.mdEstadio=t,angular.element("#mdConfirmaDelete").modal("show")},e.deleteEstadio=function(a){t["delete"]("/admin/delete_estadio/"+a.id).success(function(){angular.element("#mdConfirmaDelete").modal("hide"),e.getEstadios()})}}fnSistemaController.$inject=["$scope","$http"],angular.module("app").controller("sistemaController",fnSistemaController);