function fnSistemaController(e,n){e.getMensagens=function(){n.get("/mensagem/get_all").success(function(n){e.listaMensagens=n})},e.getMensagens(),e.saveMensagem=function(){n.post("/admin/save_mensagem",e.mensagem).success(function(n){e.mensagem={},e.avisoMensagem={"class":"alert-success",mensagem:"A mensagem foi GRAVADA com sucesso"},angular.element("#avisoMensagem").fadeIn("slow")})},e.abrirMensagem=function(n){e.mensagemDetalhe=n,angular.element("#mdDetalheMensagem").modal("show")},e.editaMensagem=function(n){angular.element(document).scrollTop(0),e.mensagem=n,console.info(n)},e.arquivaMensagem=function(s){n.put("/admin/arquiva_mensagem/"+s.id).success(function(n){e.getMensagens()})}}fnSistemaController.$inject=["$scope","$http"],angular.module("app").controller("sistemaController",fnSistemaController);